pragma solidity ^0.5.0;

import "./FlipCoin.sol";

contract EthSwap {
  string public name = "FlipCoin Exchange";
  FlipCoin public flipcoin;
  uint public rate = 100;

  event CoinsPurchased(
    address account,
    address coin,
    uint amount,
    uint rate
  );

  event CoinsSold(
    address account,
    address coin,
    uint amount,
    uint rate
  );

  constructor(FlipCoin _coin) public {
    flipcoin = _coin;
  }

  function buyFlipCoins() public payable {
    // Calculate the number of flipcoins to buy
    uint flipcoinAmount = msg.value * rate;

    // Require that EthSwap has enough flipcoins
    require(flipcoin.balanceOf(address(this)) >= flipcoinAmount);

    // Transfer flipcoins to the user
    flipcoin.transfer(msg.sender, flipcoinAmount);

    // Emit an event
    // emit flipcoinsPurchased(msg.sender, address(flipcoin), flipcoinAmount, rate);
  }

  function sellFlipCoins(uint _amount) public {
    // User can't sell more flipcoins than they have
    require(flipcoin.balanceOf(msg.sender) >= _amount);

    // Calculate the amount of Ether to redeem
    uint etherAmount = _amount / rate;

    // Require that EthSwap has enough Ether
    require(address(this).balance >= etherAmount);

    // Perform sale
    flipcoin.transferFrom(msg.sender, address(this), _amount);
    msg.sender.transfer(etherAmount);

    // Emit an event
    // emit flipCoinsSold(msg.sender, address(flipcoin), _amount, rate);
  }

}
